import React from 'react'; 
import Journalist from './Journalist';
import Article from '../articles/Article';
import { Link } from 'react-router-dom';

const JournalistDetail = ({ journalist, onDelete }) => {
    if (!journalist) {
        return <p>Loading...</p>
    }

    const handleDelete = () => {
        onDelete(journalist.id)
    }

    const editUrl = "/journalists" + journalist.id + "/edit"

    return (
        <div className="component">
            <Journalist journalist={journalist}/>
            <p>Journalist: {journalist.name}</p>
            <p>{journalist.image}</p>
            <p>Articles: {journalist.articles}</p>
            <button onClick={handleDelete}>Delete {journalist.name}</button>
            <Link to={editUrl}><button type="button"> Edit {article.name}</button></Link>
        </div>
    )

}

export default ArticleDetail;
