import Article from './Article';
import { Link } from 'react-router-dom';

const ArticleDetail = ({ article }) => {
    if (!article) {
        return <p>Loading...</p>
    }

    const handleDelete = () => {
        onDelete(article.id)
    }

    const editUrl = "/articles" + article.id + "/edit"

    return (
        <div className="component">
            <Article article={article} />
        </div>
    )

}

