import React, { useState, useEffect } from 'react';
// import ImageUploader from 'react-images-upload';

const ArticleForm = ({ article, onCreate, onUpdate }) => {

    const [stateArticle, setStateArticle] = useState(
        {
            headline: "",
            summary: "",
            fullStory: "",
            category: "",
            location: "",
            date: ""

        }
    )

    const handleChange = function (event) {
        let propertyName = event.target.name;
        let copiedArticle = { ...stateArticle }
        copiedArticle[propertyName] = event.target.value;
        setStateArticle(copiedArticle)
    }

    const handleSubmit = function (event) {
        event.preventDefault();
        if (statePirate.id) {
            onUpdate(statePirate)
        } else {
            onCreate(statePirate);
        }
    }

}
